.herosect {
    height: 100vh;
    min-height: 550px;
    position: relative;
    overflow: hidden;
    padding-top: 135px;
    display: flex;
    align-items: center;
    justify-content: center;

    &::after {
        content: '';
        display: block;
        position: absolute;
        left: 0;
        top: 0;
        width: 100%;
        height: 100%;
        background: rgba(#000, 0.5);
    }

    .container {
        position: relative;
        z-index: 1;
    }

    h1 {
        text-align: center;
        color: #fff;
        text-transform: uppercase;
        font-size: 58px;
        font-family: "Tenor Sans", sans-serif;
        line-height: 110%;
        font-weight: 400;
        margin: -200px 0 0 0;
    }
}

.herosect__bg {
    width: 100%;
    height: 100%;
    object-fit: cover;
    object-position: 50% 50%;
    position: absolute;
    left: 0;
    top: 0;
}

.herosect__formwrap {
    position: absolute;
    bottom: 80px;
    left: 0;
    width: 100%;
}

.herosect__form {
    background: rgba(#fff, 0.26);
    border-radius: 130px;
    display: flex;
    flex-wrap: wrap;
    padding: 22px 30px;
    backdrop-filter: blur(5px);
    border: 1px solid #C9A96D;


    .herosect__formleft {
        width: 100%;
        max-width: 29%;
        flex: 0 0 29%;

        p {
            margin: 0;
            font-size: 19px;
            color: #fff;
            font-weight: 500;
            line-height: 120%;
            text-transform: uppercase;
        }
    }

    .herosect__formright {
        width: 100%;
        max-width: 71%;
        flex: 0 0 71%;
        padding-left: 50px;

        form {
            display: flex;
            flex-wrap: wrap;
            justify-content: space-between;
            align-items: center;

            .inputfield {
                width: 100%;
                max-width: 237px;
                flex: 0 0 237px;

                input {
                    height: 48px;
                    background: transparent;
                    border: none;
                    width: 100%;
                    border-bottom: 1px solid #fff;
                    font-family: "Montserrat", sans-serif;
                    font-size: 16px;
                    color: #fff;
                    transition: 0.3s;

                    &::placeholder {
                        color: rgba(#fff, 0.8);
                    }

                    &:focus {
                        border-bottom: 1px solid #bb9247;
                    }
                }
            }

            .herosect__submit {
                height: 48px;
                width: 100%;
                max-width: 165px;
                flex: 0 0 165px;
                background: #C9A86C;
                border-radius: 24px;
                border: none;
                padding: 3px 10px;
                color: #fff;
                font-weight: 500;
                font-family: "Montserrat", sans-serif;
                font-size: 16px;
                cursor: pointer;
                transition: 0.3s;

                &:hover {
                    background: #bb9247;
                }
            }
        }
    }
}

// Ексклюзивний комплекс
.exclusivecomplex {
    padding: 160px 0 630px;
    background-image: url('../img/main/pattern.png');
    background-repeat: repeat-x;
    position: relative;
    background-color: #FAF9F4;

    h2 {
        margin-bottom: 50px;
    }

    .descr {
        max-width: 525px;

        p {
            margin: 0;
            font-size: 17px;
            line-height: 150%;
        }

        a {
            display: inline-flex;
            align-items: center;
            margin-top: 35px;
            color: #C9A86C;
            font-size: 17px;
            font-weight: 600;
            transition: 0.3s;

            svg {
                margin-left: 15px;

                path {
                    transition: 0.3s;
                }
            }

            &:hover {
                color: #bb9247;

                svg {
                    path {
                        animation: move 2s infinite;
                    }
                }
            }
        }
    }

    .container {
        position: relative;
        z-index: 1;
    }
}

.exclusivecomplex__bg {
    position: absolute;
    bottom: 0;
    left: 0;
    height: 694px;
    width: 100%;

    img {
        width: 100%;
        height: 100%;
        object-fit: cover;
        object-position: 0 0;
        vertical-align: top;
    }
}

.infonum {
    display: flex;
    flex-wrap: wrap;
    align-items: flex-start;

    .infonum__box {
        width: 100%;
        max-width: 33.333%;
        flex: 0 0 33.333%;
        text-align: center;

        span {
            font-size: 80px;
            color: #C9A86C;
            line-height: 110%;
            font-family: "Tenor Sans", sans-serif;

            i {
                font-size: 30px;
            }
        }

        p {
            margin: 5px 0 0 0;
            text-transform: uppercase;
            font-size: 15px;
            color: #2A332D;
            line-height: 110%;
        }
    }
}

.exclusivecomplex__bottomic {
    position: absolute;
    bottom: 85px;
    left: 0;
    width: 100%;

    .box {
        width: 160px;

        img {
            width: 100px;
        }

        p {
            margin: 15px 0 0 0;
            text-transform: uppercase;
            font-weight: 500;
            color: #fff;
            font-size: 16px;
        }
    }
}

// Інвестиційний потенціал
.investsect {
    padding: 160px 0 0 0;
    background: #2A332D;

    .investsect__topsect {
        padding-bottom: 45px;
    }

    .descr {
        p {
            margin-bottom: 20px;
            font-size: 17px;
            color: #fff;

            b {
                font-weight: 600;
            }
        }

        a {
            margin-top: 30px;

            &:hover {
                animation: pulse 2s infinite;
            }
        }
    }

    .investsect__img {
        height: 640px;
        position: relative;

        img {
            width: 100%;
            height: 100%;
            object-fit: cover;
            object-position: 50% 50%;
            position: absolute;
            left: 0;
            top: 0;
        }

        &::after {
            content: '';
            display: block;
            width: 100%;
            height: 100%;
            position: absolute;
            left: 0;
            top: 0;
            background: rgba(#000, 0.38);
            z-index: 1;
        }
    }

}

.investsect__circles {
    position: absolute;
    max-width: 518px;
    width: 100%;
    left: calc((100vw - 1444px) / 2);
    top: 0;

    .circleboxwrap {
        margin-top: -120px;
    }
}

.circleboxwrap {
    background: #2A332D;
    border-radius: 50%;
    width: 233px;
    height: 233px;
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
    position: absolute;
    z-index: 2;

    &:nth-child(1) {
        left: 0;
    }

    &:nth-child(2) {
        right: 0;
    }

    .circlebox {
        width: calc(100% - 34px);
        height: calc(100% - 34px);
        border: 1px solid #C9A86C;
        border-radius: 50%;
        text-align: center;
        padding-top: 45px;

        p {
            width: 100%;
            max-width: 100%;
            flex: 0 0 100%;
        }

        .title {
            color: #fff;
            margin-bottom: 10px;
            text-transform: uppercase;
            line-height: 110%;
            font-size: 16px;
        }

        .inf {
            text-transform: uppercase;
            font-size: 14px;
            line-height: 110%;
            color: #C9A86C;

            span {
                font-size: 57px;
            }

            i {
                font-size: 24px;
                font-style: normal;
            }
        }
    }

}

.investsect__img-info {
    position: absolute;
    bottom: 104px;
    width: 100%;
    z-index: 2;
    text-align: right;

    .infwrap {
        max-width: 480px;
        width: 100%;
        display: inline-block;
        text-align: left;

        p {
            color: #fff;
            text-transform: uppercase;
            font-size: 23px;
            font-family: "Tenor Sans", sans-serif;
        }

        a {
            font-size: 17px;
            color: #FFD995;
            font-weight: 600;
            display: inline-flex;
            align-items: center;
            transition: 0.3s;

            svg {
                margin-left: 15px;
            }

            &:hover {
                color: #bb9247;

                svg {
                    path {
                        animation: move 2s infinite;
                    }
                }
            }
        }
    }
}

// Котеджі
.cotadges {
    padding: 160px 0;
    position: relative;
    background-image: url(../img/main/pattern.png);
    background-repeat: repeat-x;
    background-position: left bottom;
    background-color: #FAF9F4;

    .absel1 {
        position: absolute;
        right: 0;
        top: 0;
    }

    .absel2 {
        position: absolute;
        left: 0;
        top: 228px;
    }
}

.cotadges__top {
    text-align: center;
    margin-bottom: 70px;

    h2 {
        margin-bottom: 50px;
        font-size: 55px;
    }

    p {
        margin: 0 auto;
        max-width: 705px;
        font-size: 17px;
        color: #231F20;
    }
}

.cotadges__body {
    display: flex;
    flex-wrap: wrap;
    justify-content: space-between;
    position: relative;
    z-index: 2;

    .cotadgebox {
        width: 100%;
        max-width: 30%;
        flex: 0 0 30%;
        position: relative;

        &:hover {
            .hovercot {
                opacity: 1;
            }

            .cotadgebox__img img {
                transform: scale(1.01);
            }
        }

        &:nth-child(2) {
            margin-top: 60px;
        }
    }

    .cotadgebox__img {
        height: 572px;
        position: relative;
        display: block;
        overflow: hidden;

        img {
            width: 100%;
            height: 100%;
            object-fit: cover;
            object-position: 50% 50%;
            transform: scale(1.1);
            transition: 0.5s;
        }
    }

    .cotadgebox__descr {
        padding-top: 20px;
        padding-left: 10px;
        display: flex;
        align-items: center;
        flex-wrap: wrap;

        .title {
            width: 100%;
            max-width: calc(100% - 120px);
            flex: 0 0 calc(100% - 120px);
            font-size: 20px;
            position: relative;
            color: #231F20;
            padding-left: 33px;
            margin: 0;

            &::after {
                content: '';
                display: inline-block;
                width: 24px;
                height: 24px;
                background: url('../img/main/star.svg') no-repeat center / 100%;
                position: absolute;
                left: 0;
                top: 3px;
            }
        }

        .sq {
            width: 100%;
            max-width: 120px;
            flex: 0 0 120px;
            text-align: right;
            margin: 0;
            font-size: 20px;
            color: #231F20;
        }
    }

    .hovercot {
        position: absolute;
        left: 0;
        top: 0;
        width: 100%;
        height: 100%;
        background: rgba(#000, 0.42);
        display: flex;
        align-items: center;
        justify-content: center;
        backdrop-filter: blur(5px);
        opacity: 0;
        transition: 0.7s;
        cursor: pointer;

        .hovercotbox {
            text-align: center;

            img {
                width: 67px;
                margin-bottom: 17px;
            }

            p {
                margin: 0;
                color: #C9A86C;
                font-size: 17px;
            }
        }
    }

}

.hidden-gallery-images {
    display: none;
}

.cotadges__btns {
    text-align: center;
    margin-top: 80px;

    a {
        margin: 0 15px;
        font-size: 17px;
    }
}

// інфраструктура

.infrastucture {
    padding: 160px 0 0 0;

    .descr {
        p {
            font-size: 17px;
            color: #231F20;
        }
    }

    .arrlink {
        margin-top: 10px;
    }
}

.infrastucture__top {
    margin-bottom: 60px;
}

.infrtabsheader {
    display: flex;
    flex-wrap: wrap;
    justify-content: space-between;

    button {
        padding: 0;
        border: none;
        outline: none;
        background: transparent;
        color: rgba(#231F20, 0.55);
        font-family: "Tenor Sans", sans-serif;
        font-size: 21px;
        line-height: 110%;
        text-transform: uppercase;
        transition: 0.3s;
        cursor: pointer;

        &::before {
            content: '';
            display: inline-block;
            width: 21px;
            height: 21px;
            display: block;
            background: url('../img/main/star.svg') no-repeat center / 20px;
            margin: 0 auto;
            opacity: 0;
            transition: 0.3s;
            margin-bottom: 10px;
        }

        &.active {
            color: rgba(#231F20, 1);

            &::before {
                opacity: 1;
            }

        }

        &:hover {
            color: #bb9247;
        }
    }
}

.infrtabsheader {
    margin-bottom: 50px;
}

.infrtabsbody {
    overflow: hidden;

    .swiper {
        .imgwrap {
            height: 590px;
            position: relative;

            img {
                width: 100%;
                height: 100%;
                object-fit: cover;
                object-position: 50% 50%;
            }
        }
    }
}

.slidernavbox-wrap {
    position: absolute;
    width: 100%;
    left: 0;
    bottom: 50px;
    z-index: 2;


    .slidernavbox {
        width: 175px;
        position: relative;
        display: flex;
        justify-content: space-between;
        align-items: center;
    }

    .swiper-button-prev,
    .swiper-button-next {
        position: relative;
        margin: 0;
        opacity: 1;
        z-index: 11;
        top: initial;
        width: auto;
        height: auto;

        &::after {
            display: none;
        }

        svg {
            circle {
                transition: 0.3s;
            }
        }

        &:hover {
            svg {
                circle {
                    fill: rgba(#2A332D, 0.8);
                }
            }
        }
    }

    .swiper-button-prev {
        left: 0;
        right: initial;
    }

    .swiper-button-next {
        right: 0;
        left: initial;
    }

    .swiper-pagination {
        position: relative;
        width: auto;
        top: initial;
        bottom: initial;
    }

    .swiper-pagination-current {
        color: #FFD995;
        font-size: 25px;
        font-family: "Tenor Sans", sans-serif;
    }
}

