.faqheader {
    min-height: 100px;
    padding: 10px 0;
    display: flex;
    align-items: center;
    cursor: pointer;
    padding-right: 70px;
    position: relative;

    &.active {
        &::after {
            transform: rotate(-180deg);
        }

        p {
            color: #C9A86C;
        }
    }

    &::after {
        content: '';
        display: inline-block;
        background: url('../img/main/faq-arrow.svg') no-repeat center / 100%;
        position: absolute;
        right: 0;
        top: 37px;
        width: 30px;
        height: 30px;
        transition: 0.3s;
    }

    p {
        font-family: "Tenor Sans", sans-serif;
        margin: 0;
        color: #231F20;
        font-size: 21px;
        line-height: 110%;
        text-transform: uppercase;
    }
}

.faqbody {
    display: none;
    padding: 10px 0 30px 40px;

    p {
        margin: 0;
        font-size: 17px;
        color: #231F20;
    }
}

