+.infrastrsect {
+    padding: 160px 0;
+    background-image: url(../img/main/pattern.png);
+    background-repeat: repeat-x;
+}
+
+.infrastrbox {
+    display: flex;
+    flex-wrap: wrap;
+    margin-bottom: 190px;
+
+    &:last-child {
+        margin-bottom: 0;
+    }
+
+    .infrastrbox__left {
+        width: 100%;
+        max-width: 220px;
+        flex: 0 0 220px;
+
+        p {
+            font-family: "Tenor Sans", sans-serif;
+            margin: 0;
+            color: #C9A86C;
+            font-size: 30px;
+            line-height: 110%;
+            padding-top: 10px;
+        }
+    }
+
+    .infrastrbox__right {
+        width: 100%;
+        max-width: calc(100% - 220px);
+        flex: 0 0 calc(100% - 220px);
+        overflow: hidden;
+
+        h2 {
+            font-family: "Tenor Sans", sans-serif;
+            font-size: 70px;
+            line-height: 110%;
+            color: #2A332D;
+            margin-bottom: 60px;
+        }
+
+        .descr {
+            display: flex;
+            flex-wrap: wrap;
+            align-items: center;
+            padding-bottom: 60px;
+            position: relative;
+
+            .imgwrap {
+                width: 100%;
+                max-width: 132px;
+                flex: 0 0 132px;
+
+                img {
+                    max-width: 100%;
+                    max-height: 100%;
+                }
+            }
+
+            .textwrap {
+                width: 100%;
+                max-width: calc(100% - 132px);
+                flex: 0 0 calc(100% - 132px);
+
+                p {
+                    max-width: 840px;
+                    font-size: 17px;
+                    color: #231F20;
+
+                    &:last-child {
+                        margin: 0;
+                    }
+                }
+            }
+
+            .absimg {
+                position: absolute;
+                right: 0;
+                bottom: 0;
+            }
+        }
+
+        .imgbox {
+            height: 450px;
+            position: relative;
+
+            img {
+                width: 100%;
+                height: 100%;
+                object-fit: cover;
+                object-position: 50% 50%;
+            }
+        }
+    }
+}
+
+@media(max-width:1599.8px) {
+    .infrastrbox .infrastrbox__right h2 {
+        font-size: 50px;
+        margin-bottom: 40px;
+    }
+
+
+
+
+
+
+
+}
